
public class TA05Ejercicio7y8App {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Declaraci√≥n de las variables
		int Num = 1;
		
		//Ejercicio 7
		System.out.println("Del 1 al 100 con un while");
		while (Num <= 100) {
			System.out.println(Num++);
		}
		
		System.out.println("");
		
		//Ejercicio 8
		System.out.println("Del 1 al 100 con un for");
		for (Num = 1; Num <= 100; Num++) {
			System.out.println(Num);
		}
		
	}

}
